import BaseLayout from '../components/BaseLayout'
import Link from 'next/link';

const PostLink = (props) => (
    <li>
    <Link href={`single?title=${props.title}`} as={`/single/${props.id}`} >
        <a> {props.title} </a>
    </Link>
    </li>
);

const Blog = () => (
    <BaseLayout>
        <h1>Welcome to Blog.js</h1>
        <ul>
            <PostLink id="Artikel-Pertama" title='Artikel Pertama'/>
            <PostLink id="Artikel-Kedua" title='Artikel Kedua'/>
            <PostLink id="Artikel-Terakhir" title='Artikel Terakhir'/>
        </ul>
    </BaseLayout>
)

export default Blog;
